def Tonelli_Shanks(a, p) :
    n = a % p
    q = p - 1
    s = 0
    while q % 2 == 0:
        q = q // 2
        s += 1

    k = 2
    while pow(k, (p - 1) // 2, p) == 1:
        k += 1
    z = k

    m = s
    c = pow(z, q, p)
    t = pow(n, q, p)
    r = pow(n, (q + 1) // 2, p)

    while True:
        if t == 0:
            return 0
        elif t == 1:
            return r
        else:
            i = 0
            for i in range(1, m):
                if pow(t, pow(2, i), p) == 1:
                    break
            b = pow(c, pow(2, m - i - 1), p)
            m = i
            c = pow(b, 2, p)
            t = t * pow(b, 2) % p
            r = r * b % p

print(Tonelli_Shanks(a, p))